mapper.select:
SELECT t1.id, t1.module, t1.content, t1.request_uri, t1.ip, CONCAT(t1.province," ", t1.city) AS region, t1.execution_time, CONCAT(t1.browser," ", t1.browser_version) AS browser, t1.os, t1.create_time, t2.nickname AS operator FROM sys_log t1 LEFT JOIN sys_user t2 ON t1.create_by = t2.id
t1.is_deleted = 0
AND ( t1.content LIKE concat('%',#{queryParams.keywords},'%') OR t1.ip LIKE concat('%',#{queryParams.keywords},'%') OR t2.nickname LIKE concat('%',#{queryParams.keywords},'%') )
AND t1.create_time >= #{queryParams.startDate}
AND t1.create_time <= #{queryParams.endDate}
ORDER BY t1.create_time DESC
